package arithmaticOperators;

public class Question8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10; 

		int j = 7; 

		System.out.println(i+j); 

		System.out.println(i-j); 

		System.out.println(i*j); 

		System.out.println(i/j); 

		System.out.println(i%j);
	}

}
