
public class numbersys {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub

		/*Covert below Decimal to Octal
		 * 20
		 * 100
		 * 56
		 * 78
		 * 1453
		 * 544
		 */
        System.out.println("Decimal to Octal");

		int k = 020; //  octal 
		System.out.println(k);

		k= 0100;
		System.out.println(k);
		
		k= 056;
		System.out.println(k);
		
		k=78;
		System.out.println(k);
		
		k= 01453;
		System.out.println(k);
		
		k= 0544;
		System.out.println(k);
		
		/*Covert below Decimal to Hexadecimal
		 * 245
		 * 45
		 * 67
		 * 98
		 * 712
*/
        System.out.println("Decimal to Hexadecimal");


		int m = 0X245; // Hexadecimal
		System.out.println(m);
		
		m = 0X245; // Hexadecimal
		System.out.println(m);
		
		m = 0X45; // Hexadecimal
		System.out.println(m);
		
		m = 0X67; // Hexadecimal
		System.out.println(m);
		
		m = 0X98; // Hexadecimal
		System.out.println(m);

		m = 0X712; // Hexadecimal
		System.out.println(m);

	}

}
